INSERT INTO biobank VALUES (30, 'Biothèque Hospitalo Universitaire de Liège (BHUL)', 'The “Biothèque Hospitalo-Universitaire de Liège” (BHUL) is a biobank which is in charge of the daily collection of human biological samples in the respect of ethical, legal and quality requirements. Over many years, we have built collections of samples in various topics (oncology, neurology, inflammatory disease, genetic disease…) and storage formats (freezers, liquid nitrogen). We can offer the selection of samples meeting researcher’s specific criteria, tissue slides for staining or immunohistochemistry, sections in tubes for extraction, serum, plasma, cells… Our biobank supply biological samples for scientific research and we are proud to offer clinical and technical data when available.', 'bbmri-eric:biobankID:BE_LCHU1');
INSERT INTO biobank VALUES (46, 'Estonian Biocentre', NULL, 'bbmri-eric:biobankID:EE_EBC');
