INSERT INTO biobank VALUES (636, 'PRB-HEGP', 'Née récemment de la fédération des activités du CRB et de la Tumorothèque de l’HEGP, la nouvelle Plate-Forme de Ressources Biologiques (PRB-HEGP) articule son activité autour des deux principaux pôles d’activité de l’HEGP : l’oncologie et le cardiovasculaire. Au sein de la PRB-HEGP, le CRB est responsable de la gestion des échantillons non tissulaires à visée de recherche et la Tumorothèque de la gestion des échantillons tissulaires sains ou pathologiques collectionnés à visée de recherche ou recueillis dans un cadre de soin, mais pouvant être re-qualifiés pour la recherche.', 'bbmri-eric:ID:FR_BB-0033-00063');
INSERT INTO biobank VALUES (81, 'CRB maladies du foie', 'La principale thématique concerne les maladies chroniques du foie, en particulier la cirrhose et le dépistage et le diagnostic du carcinome hépatocellulaire (CHC).', 'bbmri-eric:biobankID:FR_BB-0033-00027');
