INSERT INTO biobank VALUES (100, 'CRB Ferdinand Cabanne - Dijon', 'Créé en 2002, le Centre de Ressources Biologiques Ferdinand Cabanne, partagé entre les institutions de santé du campus dijonnais (CHU, EFS Bourgogne Franche Comté, Centre de Lutte Contre le Cancer Georges François Leclerc), est ouvert à la communauté scientifique et médicale, au secteur privé (centre de pathologie) et aux biotechnologies de santé, comme infrastructure de support à la recherche biomédicale au niveau de l’inter-région Bourgogne – Franche-Comté. Le CRB est intégré au plateau technique de biologie Gérard Mack (plateforme de génétique moléculaire des cancers, plateforme de cytométrie en flux et le séquenceur d’ADN).', 'bbmri-eric:biobankID:FR_BB-0033-00044');
INSERT INTO biobank VALUES (103, 'CRB-ICR', 'Le CRB-ICR est une entité propre à l’Institut Claudius Regaud. Il gère les prélèvements biologiques de l’Institut et les met à disposition des équipes de recherche internes et externes à l’ICR. Au cours des cinq dernières années, le CRB-ICR a contribué à des recherches dans le domaine du cancer du sein, des carcinomes pancréatiques, des mélanomes, des tumeurs de la tête et du cou et des tumeurs gynécologiques. Le CRB-ICR a contribué également à l''élaboration d''un fixateur sans formol (RCL2). Plus récemment, le CRB a ouvert une collection de tumeurs des tissus mous.', 'bbmri-eric:biobankID:FR_BB-0033-00047');
INSERT INTO biobank VALUES (117, 'da Vinci European BioBank', NULL, 'bbmri-eric:biobankID:IT_1383130755736614');
INSERT INTO biobank VALUES (129, 'Tumorothèque Hôpital Saint-Louis', 'La tumorothèque de l’hôpital Saint-Louis assure la collecte, extraction et conservation de tumeurs, tissus sains, sang etc. La tumorothèque est un site pilote du logiciel de  gestion TumoroteK, où une équipe d’informaticiens en assure l''évolution, la diffusion et la maintenance selon les recommandations d''organismes tels que la CNIL, l''OCDE et INCa.', 'bbmri-eric:biobankID:FR_BB-0033-00067');
INSERT INTO biobank VALUES (727, 'Doetinchem Cohort Study', 'Population based cohort, baseline 1987-1991, follow-up every five years. Fifth round ongoing (2008-2012). Lifestyle, risk factors and diseases: cardiovascular, diabetes, COPD, cancer, musculoskeletal', 'bbmri-eric:ID:NL_10');
