INSERT INTO biobank VALUES (738, 'Severely Obese Individuals Maastricht', 'Biopsies were taken during elective bariatric surgery.Blood was taken in fasted state on morning before surgery. Specimens for histological assessment are available. Other data available: BMI, age, waist-to-hip ratio, gender, blood measurements', 'bbmri-eric:ID:NL_110');
