INSERT INTO biobank VALUES (633, 'Cytothéque hémopathies malignes - HIMIP - Toulouse', 'La plate-forme a été créée pour effectuer des recherches en oncogénétique, prolifération, différenciation, apoptose et voies de signalisation afin d''identifier de nouvelles cibles thérapeutiques dans les tumeurs malignes hématologiques. Les études portent sur les mécanismes à l''origine de la maladie avec comme objectif ultime d''identifier de nouveaux traitements et d’optmiser ceux qui existent.', 'bbmri-eric:ID:FR_BB-0033-00060');
INSERT INTO biobank VALUES (655, 'CECOS Germethèque', 'GERMETHEQUE a pour vocation d’organiser la collecte, le stockage, la gestion et l’exploitation d’échantillons biologiques humains ainsi que leur valorisation, le tout répondant à des critères élevés de qualité et d’expertise exigés par la communauté internationale des chercheurs et par les industriels. GERMETHEQUE est dédié particulièrement à l''étude de la reproduction humaine et conserve gamètes, tissus germinaux, ADN, plasma séminal.', 'bbmri-eric:ID:FR_BB-0033-00081-0');
